sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	gs.info("About to execute BR!!!");
    try {
        gs.info("ipaddress は ===> " + current.ip_address);
        // Add your code here
        var r = new sn_ws.RESTMessageV2('x_898752_iplookup.IPInfo', 'GetIPInfo');
        r.setStringParameterNoEscape('ipaddress', current.ip_address);
        r.setStringParameterNoEscape('specific_field', 'geo');
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        // Convert the JSON formatted string to a JavaScript Object
        var responseObj = JSON.parse(responseBody);
        gs.info("responseObj ===> " + responseObj);
        // set the value of the City filed on the IPAddressInfo record
        current.city = responseObj.city;
        gs.info("City ===> " + current.city);
        current.update();
    } catch (err) {
        gs.info("Uncaught error: " + err);
        var message = err.message;
    }

})(current, previous);
